sections.css:
$small-quote-size: 1em;
$small-quote-width: 0.5em;

blockquote,
.blockquote {
  max-width: 24em;
  border-left: 0.417em solid $yellow-light;
  font-style: italic;
  font-size: $font-size-big;
  line-height: 1.5;
  margin-left: 0;
  margin-right: 0;
  padding: 0.6em 0 0.6em 1.2em;
  clear: both;
  position: relative;
  overflow: hidden;
  display: inline-block;

  // Drupal fix.
  p {
    line-height: inherit;
    display: inline;
  }
  &:before {
    content: open-quote;
  }

  &:after {
    content: close-quote;
  }
}

.blockquote--small {
  color: $text-color;
  font-family: $font-family-base;
  font-size: $font-size-little;
  font-style: normal;
  margin-bottom: 0;
  padding-left: 0;
}

h1,
h2,
h3,
h4,
h5,
h6,
.h1,
.h2,
.h3,
.h4,
.h5,
.h6 {
  margin: 0;
  padding: 0.5em 0;
}

h1,
.h1 {
  color: $grey-base;
  font-size: $font-size-h1;
  font-weight: $font-weight-bold;
}

h2,
.h2 {
  color: $grey-base;
  font-size: $font-size-h2;
  font-weight: $font-weight-bold;
}

h3,
.h3 {
  color: $gray-darker;
  font-size: $font-size-h3;
  font-weight: $font-weight-bold;
}

h4,
.h4 {
  color: $gray-dark;
  font-size: $font-size-h4;
}

h5,
.h5 {
  color: $gray-darker;
  font-size: $font-size-h5;
  font-weight: $font-weight-bold;
}

h6,
.h6 {
  color: $gray-darker;
  font-size: $font-size-h6;
  text-decoration: uppercase;
}

form {
  .label {
    @extend .form_label;
  }
}


label {
  @extend .form_label;
}

.form_label {
  color: $form-control-text-color;
  font-size: $font-size-little;
  display: block;
  text-align: left;
  padding: 0;
  margin-bottom: 0.556em;
  &.is-disabled {
    color: $form-control-color-disabled;
  }
}

.legend {
  font-size: 12px;// $form-item-legend-font-size;
}

legend {
  @extend .legend;
}

a {
  &:hover,
  &:active,
  &:focus {
    color: $grey-base;
    text-decoration: underline;
  }

  &[href^="http"]:not(.is-internal) {
    @extend .is-external;
  }
  @media print {
    &:after {
      content: none !important;
    }
  }
}

p a {
  text-decoration: underline;
}

.is-external {
  position: relative;
  &:after {
    @extend .icon--after;
    @extend .icon--external;
    font-size: 75%;
    margin-left: 0.4em;
    margin-right: 0.2em;
  }
}

// External link specific adjustments
.page-content {
  ul,
  p {
    a {
      &[href^="http"]:not(.is-internal) {

        &.btn {
          padding-right: 2.1em;
        }
      }
    }
  }
}

ul,
ol {
  padding-left: 18px;
}

.region-content {
  ul,
  ol {
    @extend .paragraph;
    margin-left: 0;
    margin-right: 0;
    margin-bottom: 30px;
    padding-left: 18px;
  }
}

.paragraph {
  margin-bottom: 0.8em;
  max-width: 32em;
}

p {
  @extend .paragraph;
}

.paragraph--width-auto {
  width: auto;
}

.select {
  -webkit-appearance: none;
  -moz-appearance: none;
  text-indent: 0.01px;
  text-overflow: '';

  // Hover state.
  &:hover,
  &.is-hovered {
    border-color: $form-control-border-select-color-hover;
  }

  // Down state.
  &.is-down {
    border-color: $form-control-border-select-color-down;
  }
}

select {
  @extend .select;
}

$quote_border_offset: 0.556em;

.banner {
  margin-top: 1.667em;
}

.banner--quote {
  border-left: $quote_border_offset solid $yellow-light;
  padding-left: $quote_border_offset;
}

.banner__author {
  font-size: $font-size-xsmall;
  font-weight: $font-weight-bold;
  padding-bottom: 0.6em;

  &:before {
    content: ' - ';
  }
}

.banner--video {
  @extend .row;
}

.banner__video {
  & .file {
    padding: 0;
    margin-bottom: 0;
    max-width: 100%;
  }
  & iframe {
    width: 100%;
    display:block;
  }
}

.banner__caption {
  background: $grey-lighter;
  font-size: $font-size-xsmall;
  padding: 0.500em 1em;
  width: 100%;
  max-width: 100%;
}

.banner__paragraph {
  @extend .col-md-6;
  font-size: $font-size-little;
}

.banner__left{
  @extend .col-md-6;
}

/*
 * Please
 * @see http://bootsnipp.com/snippets/featured/funky-radio-buttons
 * @see http://webdesign.tutsplus.com/tutorials/quick-tip-easy-css3-checkboxes-and-radio-buttons--webdesign-8953
 */

// Component
.checkbox {
  margin-bottom: 2px;
  margin-top: 0;

  label {
    color: $form-checkbox-color-unchecked;
    height: $form-control-height;
    line-height: $form-control-height;
    display: block;
    text-align: left;
    border: transparent solid 2px;
    border-radius: 0;
  }

  input {
    margin: 1px 0 0 13px;
  }

  // Checkbox.
  input[type="checkbox"] {
    height: $form-control-height;
    margin-left: -9999px;
    position: relative;
    float: left;

    // Default state.
    &:not(checked)+label {
      background-color: $form-checkbox-background-color-unchecked;
      padding-left: 2.778em;

      &:before {
        content: '';
        position: absolute;
        display: block;
        width: $form-checkbox-icon-size;
        height: $form-checkbox-icon-size;
        top: 0.8em;
        left: 1em;
        background: url('../images/svg/checkbox-unchecked.svg') no-repeat;
        background-size: auto $form-checkbox-icon-size;
      }
    }

    // Checked state.
    &:checked+label {
      color: $form-checkbox-color-checked;
      background-color: $form-checkbox-background-color-checked;

      &:before {
        background-image: url('../images/svg/checkbox-checked.svg');
      }
    }

    // Hover state, not checked.
    &:hover:not(checked)+label {
      color: $form-checkbox-color-unchecked-hover;
      border: $form-checkbox-border-color-unchecked-hover solid 2px;
      background-color: $form-checkbox-background-color-unchecked-hover;
    }

    // Hover state, checked.
    &:hover:checked+label {
      color: $form-checkbox-color-checked-hover;
      border: $form-checkbox-border-color-checked-hover solid 2px;
      background-color: $form-checkbox-background-color-checked-hover;
    }
  }
}

// Turning off completely custom checkboxes when SVG's are not available
.no-svg {
  .checkbox {
    label {
      height: auto;
      line-height: inherit;
      display: inline;
      background: none;
      padding-left: 0;
    }
    input[type="checkbox"] {
      margin: 0;
      float: none;
      height: auto;
      &:before {
        display: none;
      }

      &:not(checked)+label {
        background: none;
        padding: 0;
        &:before {
          display: none;
        }
      }

      &:checked+label {
        color: inherit;
        background: none;

        &:before {
          display: none;
        }
      }
    }
  }
}

.comments-counter {
  background: url('../images/svg/comments.svg') no-repeat center left;
  background-size: 0.889em;
  margin-top: 0.556em;
  padding-left: 1.389em;
  font-size: $font-size-xsmall;
  color: $brand-primary;
  display: block;
}

// Local Variables
$datepicker-color-header: #393939;
$datepicker-color-select: #4f4f4f;
$datepicker-color-select-separator: #686868;
$datepicker-color-background: #eaeaea;

// Component
#ui-datepicker-div {
  border: none;
  width: 100%;
}

.ui-datepicker {
  border: none;
  border-radius: 0;
  font-family: $font-family-sans-serif;
  padding: 0;


  // Datepicker header
  .ui-datepicker-header {
    background: $datepicker-color-header;
    border: none;
    border-radius: 0;
    padding: 0 !important;
    height: 50px;
    line-height: 50px;

    .ui-datepicker-prev,
    .ui-datepicker-next {
      display: block;
      height: 100%;
      width: 50px;
      top: 0;
      background: none;
      border: none;
      &:hover {
        cursor: pointer;
      }

      .ui-icon{
        display: block;
        height: 100%;
        width: 100%;
        left: 0;
        top: 0;
        margin: 0;
        background: url('../images/svg/arrow.svg') center center no-repeat;
        background-size: 7px;
       }
    }

    .ui-datepicker-prev {
      left: 0;

      .ui-icon {
        @include rotate(-180deg);
      }
    }
    .ui-datepicker-next {
      right: 0;
    }


    .ui-datepicker-title {
      margin: 0 50px;
      height: 100%;

      span,
      select {
        color: #fff;
      }

      span {
        font-weight: normal;
        font-size: 0.8em;
      }

      select {
        background: $datepicker-color-select;
        color: #fff;
        border: none;
        height: 100%;
        width: 49% !important;
        margin: 0;
        display: inline-block;
        float: none;
        padding-right: 0;
        @extend .form-control;

        &:first-child {
          border-right: 2px solid $datepicker-color-select-separator;
        }

        &:hover,
        &:active,
        &:focus {
          color: #fff;
        }
      }
    }
  }

  table.ui-datepicker-calendar {
    background: $datepicker-color-background;
    border-right: 10px solid transparent;
    border-left: 10px solid transparent;
    border-bottom: 10px solid transparent;

    th {
      font-size: 16px;
      font-weight: normal;
      background: none;
    }
  }

  .ui-state-default {
    border: none;
    background: #fff;
    color: #404040;
    text-align: center;
    margin: 1px;
    padding: 5px 10px;
  }

  .ui-state-active {
    border: 2px solid #0b64b2;
    padding: 3px 10px;
  }

  .ui-state-highlight {
    background: #fed500;
    color: #000;
  }
}

.no-svg {
  .ui-datepicker-prev {
    .ui-icon {
      background-image: url('../images/png/arrow-left.png');
    }
  }
  .ui-datepicker-next {
    .ui-icon {
      background-image: url('../images/png/arrow.png');
    }
  }
}

@include breakpoint(screen-xs-min) {
  #ui-datepicker-div {
    width: auto;
  }

  .ui-datepicker {
    table.ui-datepicker-calendar {
      width: auto !important;
    }
  }
}

@media (max-width: 479px) {
  #ui-datepicker-div {
    left: 0 !important;
  }
}

.expandable__content {
  float: left;
  width: 100%;
  background-color: $grey-lightest;
}

.expandable__toggle {
  float: left;
  width: 100%;
  background: $yellow-light;

  &.collapsed {
    background: none;
  }

  .icon {
    margin-right: 0.833em;
  }

  h3 {
    font-size: $font-size-mid;
  }

  &:hover {
    background: $yellow-light;
  }
}

.facet-tag {
  display: inline-block;
  margin-top: 0.5em;
  margin-right: 0.5em;

  & > * {
    display: inline-block;
  }
  @extend .hidden-print;
}

.facet-tag__value {
  background: $grey-light;
  padding: 0.1em 0.5em;
  margin-left: 0.5em;
  font-weight: bold;
  border-radius: 0.9em;
}

.featured-item {
  background-color: $grey-lighter;
  padding: 15px 0px;
  border-left: 0.556em solid $yellow-light;
  width: 100%;
  float: left;
  margin-bottom: 1.667em;
}

.featured-item__image {
  @extend .col-md-5;

  picture {
    img {
      width: 100%;
    }
  }
}

.featured-item__content {
  @extend .col-md-7;

  p {
    font-size: $font-size-little;
  }

  h3 {

    a {
      color: $grey-base;
    }
  }
}

.feedback-message {
  color: $grey-dark;
  max-width: none;

  &.is-disabled {
    color: $form-control-color-disabled;
  }

  &.is-error {
    background-color: $grey-light;
    padding: 12px;

    // Error icon.
    &:before {
      content: '';
      background: url('../images/svg/error.svg') no-repeat;
      background-size: 1.5em;
      width: 1.5em;
      height: 1.5em;
      margin-right: 12px;
      display: inline-block;
      margin-bottom: -4px;
    }
  }
  @extend .hidden-print;
}

@include breakpoint(screen-xs-min) {
  .feedback-message {
    &.is-error {
      &:before {
        margin-bottom: -6px;
      }
    }
  }
}

.no-svg {
  .feedback-message {
    &.is-error {
      &:before {
        background: url('../images/png/error.png') no-repeat;
      }
    }
  }
}

.field {
  .label-above {
    @extend .h3;
  }
}

.field--labeled {
  margin-bottom: 0.556em;
  overflow: hidden;

  &:last-child {
    margin-bottom: 0;
  }

  .field__label {
    display: block;
    clear: both;
    font-size: $font-size-little;
  }

  .field__items {
    display: block;
  }
}

.field--full-width {
  margin-left: -$grid-gutter-width / 2;
  margin-right: -$grid-gutter-width / 2;
  padding-left: $grid-gutter-width / 2;
  padding-right: $grid-gutter-width / 2;

  & > * {
    margin-left: auto;
    margin-right: auto;
    padding-left: 0;
    padding-right: 0;
    @extend .container-fluid;
  }
}

@include breakpoint(screen-xs-min) {
  .field--labeled {
    margin-left: -15px;
    margin-right: -15px;
    .field__label,
    .field__items {
      float: left;
      position: relative;
      min-height: 1px;
      padding-left: 15px;
      padding-right: 15px;
    }
    .field__label {
      width: 33.3%;
    }

    .field__items {
      width: 66.6%;
    }
  }
}

@include breakpoint(screen-sm-min) {
  .field--labeled {
    .field__label {
      width: 25%;
    }

    .field__items {
      width: 75%;
    }
  }
}

@include breakpoint(screen-lg-min) {
  .field--full-width {
    margin-left: 0;
    margin-right: 0;
    padding-left: 0;
    padding-right: 0;
  }

  // Bootstrap specific when in 9-sized column that is next to 3-sized column
  .col-lg-3 + .col-lg-9 {
    .field--full-width {
      position: relative;
      left: -25%;
      width: 125%;
    }
  }
}

/*
Field labeled align

Labeled align modifier should be used for aligning fields that do not have a label
with fields that do have a label.

Markup: html/field--labeled-align.html

Styleguide field.labeledalign
*/
.field--labeled-align {
}

@include breakpoint(screen-xs-min) {
  .field--labeled-align {
    margin-left: -15px;
    margin-right: -15px;
    overflow: hidden;

    .field__items {
      width: 66.6%;
      margin-left: 33.3%;
      padding: 0 15px;
      float: left;
    }
  }
}

@include breakpoint(screen-sm-min) {
  .field--labeled-align {
    .field__items {
      width: 75%;
      margin-left: 25%;
      padding: 0 15px;
      float: left;
    }
  }
}

.file {
  @extend .paragraph;
  background: $grey-lighter;
  padding: 1.333em 1.333em 1.333em 1.333em;
  overflow: hidden;
  @extend .hidden-print;
}

.file-video {
  background: none;
  padding: 1em 0;
}

.file__icon {
  float: left;
  margin-right: 1.333em;
  &:before {
    color: $grey;
    font-size: 2.778em;
  }
}

.file__metadata {
  line-height: 1;
}

.file__title {
  font-weight: bold;
  line-height: initial;
}

.file__language {
  font-weight: bold;
  font-size: $font-size-little;
  display: block;
  line-height: initial;
  margin-top: 0.556em;
}

.file__info {
  font-size: $font-size-xsmall;
  line-height: initial;
  margin-top: 0.556em;
}

.file__btn {
  margin-bottom: 0;
  margin-top: 0.778em;
  width: 10.5em;

  &:after {
    @extend .icon--after;
    @extend .icon--download;
  }
}

.file--widebar {
  @extend .row;
  max-width: 100%;

  margin-left: 0;

  .file__icon {
    @extend .col-xs-1;
    margin-right: 0;
    padding-left: 0;
    height: 2.556em;
    overflow: hidden;

    @media (max-width: $screen-sm-min) {
      min-width: 60px;
    }

    &:before {
      font-size: 2.556em;
    }
  }

  .file__metadata {
    @extend .col-md-7;
    @extend .col-sm-8;
    @extend .col-xs-10;
    padding-left: 0;
  }

  .file__btn {
    @extend .col-md-4;
    @extend .col-sm-3;
    @extend .col-xs-12;

    @media (max-width: $screen-sm-min) {
      margin-top: 15px;
    }
  }
}

@include breakpoint(screen-msm-min) {
  .file__metadata,
  .file__btn {
    overflow: hidden;
  }

  .file__metadata {
    float: left;
    width: 15em;
  }

  .file__btn {
    float: right;
    margin-top: 0;
  }
}

.footer {
  background: $brand-primary;
  color: #fff;
  font-size: $font-size-little;
  text-align: center;

  a {
    color: #fff;
    text-decoration: underline;

    &:hover,
    &:active,
    &:focus {
      text-decoration: none;
    }
  }
  @extend .hidden-print;
}

.footer__column-main {
  padding-bottom: 30px;
  padding-top: 30px;
}

.footer__label {
  display: block;
  font-size: 14px;
  margin-right: 20px;
}

.footer__disclaimer {
  padding-top: 8px;
  & > * {
    width: 100%;
    display: block;
  }
}

// Menus
.footer__menu {
  padding-left: 0;
  margin: 0;

  .footer__menu-item {
    margin-right: 15px;
    margin-bottom: 0.5em;
    padding: 0;

    &:first-child > a {
      padding-left: 0;
    }

    a {
      padding: 0;
      &:hover,
      &:active,
      &:focus {
        background: none;
      }
    }
  }
}

.footer__menu--separator {
  border-top: 1px solid #4d7cb4;
  margin-top: 0.6em;
  padding-top: 1.2em;
}

// Icons
.footer__icon--social {
  &:before {
    position: relative;
    top: 4px;
    margin-right: 8px !important;
  }

  &:after {
    content: none !important;
    display: none !important;
  }
}

.footer__icon--facebook {
  &:before {
    font-size: 22px;
  }
}

.footer__icon--twitter {
  &:before {
    font-size: 18px;
  }
}

// Site Switcher
.footer__site-switcher {
  background: url('../images/svg/footer__logo.svg') no-repeat center 25px $brand-political;
  background-size: 80px 40px;
  padding: 90px 30px 30px 30px;
  display: block;
  text-align: center;
  font-size: 16px;
  font-weight: bold;
}

a.footer__site-switcher {
  text-decoration: none;
  border: 2px solid transparent;

  &:hover,
  &:active,
  &:focus {
    text-decoration: underline;
  }

  &:focus {
    border: 2px solid $yellow-light;
  }
}

.no-svg {
  .footer__site-switcher {
    background-image: url('../images/png/footer__logo.png');
  }
}

@include breakpoint(screen-sm-min) {
  .footer {
    text-align: left;
  }

  // Columns
  .footer__column-main {
    padding-bottom: 60px;
    padding-top: 60px;
  }

  .footer__column-right {
    padding-bottom: 0;
    float: right;
  }

  .footer__label {
    display: inline-block;

    & ~ .footer__menu {
      display: inline-block;
    }
  }

  .footer__disclaimer {
    & > * {
      width: auto;
      display: inline-block;
      margin-right: 4px;
    }
  }
}

@include breakpoint(screen-md-min) {
  .footer__site-switcher {
    & > * {
      white-space: nowrap;
      display: inline-block;
    }
  }
}

// External link specific adjustments
.footer {
  a {
    padding-right: 1.1em !important;
    &[href^="http"]:not(.is-internal) {
      &:after {
        position: absolute;
        line-height: 2.2;
      }
    }
  }

  .footer__icon--social {
    padding-right: 0 !important;
  }

  a.footer__site-switcher {
    padding-right: 30px !important;
  }
}

.help-block {
  margin-top: 0;
  margin-bottom: 0.556em;
  font-family: $font-family-sans-serif;
  color: $form-group-help-block-text-color;
  font-size: $font-size-little;
  .disabled & {
    color: $form-control-color-disabled;
  }
  .has-error & {
    // Disable settings of base Bootstrap.
    color: $form-group-help-block-text-color-has-error;
  }
  @extend .hidden-print;
}

$icon-size: 40px;

.icon-social {
  position: relative;
  padding-left: 50px;
  padding-top: 5px;
  min-height: $icon-size;
  &:before {
    content: '';
    display: inline-block;
    width: 40px;
    height: $icon-size;
    margin: 0 10px 5px 0;
    vertical-align: middle;
    position: absolute;
    top: 0;
    left: 0;
  }

  &:hover {
    cursor: pointer;
  }
}

.icon-social--blogger {
  &:before {
    background: url('../images/svg/icons/social/blogger.svg') no-repeat;
  }

  &:hover:before {
