// in order to enable spinning apend .is-spinning class next to the .icon class
.is-spinning:before {
  @include animation(icon-spin-right 2s infinite linear);
}

@-webkit-keyframes icon-spin-right {
  0% {
    @include rotate(0deg);
  }

  100% {
    @include rotate(359deg)
  }
}

@keyframes icon-spin-right {
  0% {
    @include rotate(0deg);
  }

  100% {
    @include rotate(359deg)
  }
}

.inpage-nav__wrapper {
  margin-bottom: 1.667em;
  &.affix,
  &.affix-top {
    position:relative;
  }
  @extend .hidden-print;
}

.inpage-nav {
  .inpage-nav__list {
    margin: 0 0 0 -11px !important;
    padding: 0;
    list-style: none;
    list-style-image: none;

    li {
      &.active {
        .inpage_nav__list-item {
          border-color: $brand-primary;
          background: $grey-lighter;
        }
      }
    }
  }

  .inpage_nav__list-item {
    padding-top: 7px;
    padding-bottom: 7px;
    padding-left: 8px;
    display: block;
    color: #000;
    font-size: $font-size-little;
    font-weight: bold;
    border-left: 3px solid transparent;

    &:hover {
      background: none;
      text-decoration: underline;
    }

    &:focus {
      background: $yellow-light !important;
    }
  }
}

.inpage-nav__navbar {
  background-color: #ffffff;
  color: #ffffff;

  .inpage-nav__current-wrapper {
    overflow: hidden;
  }

  .inpage-nav__current,
  .inpage-nav__help {
    padding-bottom: 0px;
  }

  .inpage-nav__current {
    color: #ffffff;
    text-overflow: ellipsis;
    white-space: nowrap;
    display: block;
    width: 100%;
    overflow: hidden;
    font-weight: bold;
    font-size: 1em;
    cursor: pointer;
    padding-top: 1em;

    &:hover {
      color: #fff;
    }
  }

  .inpage-nav__help {
    display: none;
    background-color: $grey-lighter;
    font-size: $font-size-xsmall;
    text-transform: uppercase;
    color: $grey-dark;
    padding-top: 1.35em;
  }

  .inpage-nav__title {
    display: block;
    font-size: 1em;
    padding: 1em;
    line-height: 1;
    font-weight: bold;
  }

  .inpage-nav__toggle {
    @extend .btn;
    margin: 0;
    background-color: $blue-darkest;
    border-color: $blue-darkest;
    padding: 1em;
    &:hover,
    &:active,
    &:focus {
      background-color: $blue-darkest;
      text-decoration: none;
      color: #ffffff;
    }
  }

  .inpage-nav__icon-arrow {
    display: block;
    transition: all 0.3s ease 0s;
    transform-origin: 50% 50%;
    color: #ffffff;
    margin: 5px 4px;
    font-size: 10px;
    &:before {
      margin-right: 0px;
    }
  }

  .inpage-nav__close-text {
    display: none;
    font-size: $font-size-base;
    line-height: $line-height-computed;
  }

  .inpage-nav__header {
    margin: 0;
  }

  .inpage-nav__container {
    padding: 0;
    width: auto;
    background-color: $blue-dark;

    .navbar-collapse {
      max-height: none;
    }
  }

  .inpage-nav__list--navbar {
    padding: 0 15px;
    li {
      &.active {
        .inpage_nav__list-item {
          color: inherit;
          background: none;
        }
      }
      .inpage_nav__list-item {
        color: #fff;
        border-top: 1px solid $blue-darkest;
        padding: 0.8em 0 0.8em 1em;
        font-size: $font-size-little;

        &:hover {
          background: none;
          text-decoration: underline;
        }
      }
    }
  }
}

.inpage-nav__navbar-wrapper {
  display: none;
  &.is-active {
    display: inherit;
  }
  &.is-collapsed,
  &.is-collapsing {
    .inpage-nav__navbar {
      height: 100%;
    }
    .inpage-nav__help,
    .inpage-nav__close-text {
       display: block;
    }
    .inpage-nav__current {
     display: none;
    }
    .inpage-nav__header{
     background-color: $grey-lighter;
     font-weight: normal;
    }
    .inpage-nav__toggle {
      background-color: $grey;
      border-color: $grey;

      .inpage-nav__icon-arrow {
        @include rotate(180deg);
        color: $grey-dark;
      }
    }
  }
}

.inpage-nav__block-title {
  display: inherit;
  font-size: $font-size-base;
  text-transform: uppercase;
  font-weight: normal;
  color: $grey-dark;
}

.is-inpage-nav-open {
  overflow: hidden;

  .inpage-nav__navbar {
    overflow: auto;
  }
}

@include breakpoint(screen-md-min) {
  .inpage-nav__wrapper {
    &.affix,
    &.affix-top {
      top: 0;
    }

    &.affix {
      position: fixed;
      top: 0;
      padding-top: 30px;
    }

    &.affix-bottom {
      position: absolute;
    }
  }

  .inpage-nav__navbar-wrapper.is-active {
    display: none;
  }
}

$label_text_color_light: #fff;

.label {
  border-radius: 0;
  font-weight: normal;
  color: $grey-base;
}

.label--default {
  @extend .label-default;
  color: $label_text_color_light;
}

.label--primary {
  @extend .label-primary;
  color: $label_text_color_light;
}

.label--success {
  @extend .label-success;
  color: $label_text_color_light;
}

.label--info {
  @extend .label-info;
  color: $label_text_color_light;
}

.label--warning {
  @extend .label-warning;
  color: $label_text_color_light;
}

.label--danger {
  @extend .label-danger;
  color: $label_text_color_light;
}

.label--highlight {
  color: $grey-base;
  background-color: $yellow-light;
  &.label--meta {
    &:after{
      content: "";
      margin: 0;
    }
  }
}

.label--meta {
  @extend .meta__item;
  padding: 0.25em 0.5em 0.3em 0.5em;
  font-size: 90%;
  top: -0.075em;
  margin-right: 0.8em;
  position: relative;
}

$spacing-top-listing__title: 0.9em;
$spacing-bottom-listing__title: 0.7em;

.listing {

  &__read-more {
    text-align: right;
    font-weight: $font-weight-bold;
    display: block;
    text-transform: uppercase;


    &:after {
      @extend .icon--after;
      @extend .icon--right;
      font-size: $font-size-xsmall;
    }
  }
}

.listing__date {
  float: left;
}

.listing__column-main {
  .listing__with {
    margin-top: 0;
  }
}

.listing__wrapper {
  // margin-bottom: 1.667em;
}

.listing__wrapper--maxwidth {
  @extend .row;

  .listing {
    @extend .col-md-8;
  }
}

.listing__item {
  border-bottom: 1px solid $grey-light;
  list-style: none;
  list-style-image: none;
  padding-bottom: 0;
  padding-top: 0;

  &:first-child {
    border-top: 1px solid $grey-light;
  }

  &:hover,
  &:active,
  &:focus {
    a .listing__title,
    a.listing__title {
      text-decoration: underline;
      color: $grey-base;
    }
  }

  p {
    margin-bottom: 0;
  }
}

.listing__item-link {
  padding-top: 0.5em;
  padding-bottom: 0.5em;
  display: block;
  overflow: hidden;
  color: $grey-base;

  &:hover,
  &:active,
  &:focus {
    text-decoration: none;
  }

  &[href^="http"]:not(.is-internal) {
    &:after {
      display: none !important;
    }
    .listing__title {
      @extend .is-external;
      padding-right: 1.2em;

      &:after {
        position: absolute;
        line-height: 1.6;
      }
    }
  }
}

.listing__column-second {
  width: 100%;
  margin: 0.2em 0 0.8em 0;
}

.listing__column-second--no-link {
  padding-bottom: 0.85em;
  margin: 0;

  .btn {
    margin-bottom: 0;
  }
}

.listing__column-main {
  float: left;
  width: 100%;
  padding-left: 0;
}


.listing__title {
  color: $brand-primary;
  font-family: $font-family-sans-serif;
  font-size: 1em;
  padding: 0;
  display: inline-block;
  line-height: 1.3;
  position: relative;
  @extend .h3;

  & + * {
    font-size: $font-size-little;
    margin-top: $spacing-bottom-listing__title;
  }
}

.listing--no-padding {
  padding-left: 0 !important;
}

.listing--no-border {
  .listing__item {
    border: none !important;
  }
}

.listing--short-spaced {
  .listing__item-link {
    padding-top: 0.5em !important;
    padding-bottom: 0 !important;
  }

  .listing__item {
    &:first-child {
      .listing__item-link {
        padding-top: 0 !important;
      }
    }
  }
}

// Modifier to add line on the left
.listing--line-left {
  .listing__title {
    padding: 0 !important;
  }

  // Teaser listing
  .listing--teaser {
    padding-left: 0;
  }

  .listing__item {
    border-top: none;
    border-bottom: none;
    border-left: 5px solid $blue-dark;
    margin-top: 0.85em;
    margin-bottom: 1em;
    padding: 0.4em 0 0.4em 0.7em;

    .listing__item-link {
      padding-top: 0;
      padding-bottom: 0;
    }
  }
}

.listing--no-link {
  .listing__item {
    padding-top: 0.5em;
    padding-bottom: 0.5em;
  }
}

.listing--title-large,
.listing--title-medium {
  .listing__title {
    margin-top: 0.2em;
    margin-bottom: 0.4em;
  }
}

.listing--title-large {
  .listing__item {
    margin-bottom: 15px;
  }

  .listing__title {
    font-size: $font-size-h2;
  }
}

.listing--title-medium {
  .listing__title {
    font-size: $font-size-h3;
  }
}

.listing--person {
  .listing__item-link {
    padding-bottom: 1em;
    padding-top: 1em;
  }
  .listing__title + * {
    line-height: 1.3;
    margin-top: 0;
  }
}

.listing--nostripe {
  padding: 30px;

  .listing__item {
    border: none;

    &:first-child {
      border: none;

      .listing__item-link {
        padding-top: 0;
      }
    }

    &:last-child {
      .listing__item-link {
        padding-bottom: 0;
      }
    }
  }
}

.listing--cta {
  .listing__item-link {
    padding-right: 1.667em;
    padding-left: 1.667em;
  }
  .listing__item {
    border: none;
    .listing__title,
    a {
      background-color: $brand-primary;
      color: #ffffff;
    }

    &:hover,
    &:active,
    &:focus {
      .listing__title {
        color: #ffffff;
      }
    }
  }
  .listing__title {
    position: relative;
    padding-right: 1.3em;
    &:after {
      @extend .listing__title--arrow;
    }
  }
}

.listing__title--arrow {
  &:after {
    margin-left: 10px;
    margin-top: 2px;
    font-size: 1.2em;
    position: absolute;
    @extend .icon--after;
    @extend .icon--right;
  }
}

@include breakpoint(screen-xs-min) {
  .listing__column-second {
    float: right;
    width: 25%;
    padding-left: 15px;
    margin: 0;

    & + .listing__column-main {
      width: 75%;
    }
  }

  .listing__column-second--no-link {
    width: 100%;
    padding-left: 0;
  }
}

@include breakpoint(screen-msm-min) {
  .listing__column-main--sidebar-next {
    width: 66.6%;
  }

  .listing__column-second--no-link {
    float: right;
    width: 33.3%;
    padding-left: 15px;
    padding-top: 0.85em;

    .btn {
      margin-top: 1.667em;
    }
  }
}

/*
Listing title

Listing components

Markup: html/listing--title.html

.listing--line-left - Listing line left

Styleguide listing.title
*/
.listing--title,
ul.listing--title {
  padding: 0;

  .listing__item {
    &:hover,
    &:active,
    &:focus {
      a .listing__title {
        text-decoration: none;
      }
    }
  }

  .listing__item-link,
  .listing__title {
    padding: 0;
    margin: 0;
    display: block;
  }

  a.listing__title,
  a .listing__title {
    display: block;
    padding-top: $spacing-top-listing__title;
    padding-bottom: $spacing-bottom-listing__title;
