    }

    &.icon--check:before {
      display: none;
    }
  }

  &__btn-close {
    position: absolute;
    top: -15px;
    right: 0;
    padding: 0.5em 0 0.5em 1em;
    font-size: $font-size-little;
    font-weight: normal;
    color: #ffffff;
    display: none;

    &:hover {
      color: #ffffff;
    }

    &:after {
      content: "X";
      float: right;
      display: inline-block;
      border: 1px solid #ffffff;
      margin-left: 0.5em;
      width: 1.6em;
      height: 1.6em;
      border-radius: 1.1em;
      text-align: center;
    }
  }

  &--overlay {
    display: none;
    position: fixed;
    height: 100%;
    width: 100%;
    background: rgba(0, 68, 148, 0.95);
    overflow-x: auto;
    top: 0;
    left: 0;
    z-index: 10;
    text-align: left;

    h2 {
      display: block;
    }

    .splash-page__btn-language {
      &.is-active {
        color: #000000;
        background: $yellow-light;
        box-shadow: none;
      }
      &.icon--check:before {
        float: right;
        display: block;
      }
    }

    .splash-page__btn-close {
      display: block;
    }

  }
}

