// Local Variables
$datepicker-color-header: #393939;
$datepicker-color-select: #4f4f4f;
$datepicker-color-select-separator: #686868;
$datepicker-color-background: #eaeaea;

// Component
#ui-datepicker-div {
  border: none;
  width: 100%;
}

.ui-datepicker {
  border: none;
  border-radius: 0;
  font-family: $font-family-sans-serif;
  padding: 0;


  // Datepicker header
  .ui-datepicker-header {
    background: $datepicker-color-header;
    border: none;
    border-radius: 0;
    padding: 0 !important;
    height: 50px;
    line-height: 50px;

    .ui-datepicker-prev,
    .ui-datepicker-next {
      display: block;
      height: 100%;
      width: 50px;
      top: 0;
      background: none;
      border: none;
      &:hover {
        cursor: pointer;
      }

      .ui-icon{
        display: block;
        height: 100%;
        width: 100%;
        left: 0;
        top: 0;
        margin: 0;
        background: url('../images/svg/arrow.svg') center center no-repeat;
        background-size: 7px;
       }
    }

    .ui-datepicker-prev {
      left: 0;

      .ui-icon {
        @include rotate(-180deg);
      }
    }
    .ui-datepicker-next {
      right: 0;
    }


    .ui-datepicker-title {
      margin: 0 50px;
      height: 100%;

      span,
      select {
        color: #fff;
      }

      span {
        font-weight: normal;
        font-size: 0.8em;
      }

      select {
        background: $datepicker-color-select;
        color: #fff;
        border: none;
        height: 100%;
        width: 49% !important;
        margin: 0;
        display: inline-block;
        float: none;
        padding-right: 0;
        @extend .form-control;

        &:first-child {
          border-right: 2px solid $datepicker-color-select-separator;
        }

        &:hover,
        &:active,
        &:focus {
          color: #fff;
        }
      }
    }
  }

  table.ui-datepicker-calendar {
    background: $datepicker-color-background;
    border-right: 10px solid transparent;
    border-left: 10px solid transparent;
    border-bottom: 10px solid transparent;

    th {
      font-size: 16px;
      font-weight: normal;
      background: none;
    }
  }

  .ui-state-default {
    border: none;
    background: #fff;
    color: #404040;
    text-align: center;
    margin: 1px;
    padding: 5px 10px;
  }

  .ui-state-active {
    border: 2px solid #0b64b2;
    padding: 3px 10px;
  }

  .ui-state-highlight {
    background: #fed500;
    color: #000;
  }
}

.no-svg {
  .ui-datepicker-prev {
    .ui-icon {
      background-image: url('../images/png/arrow-left.png');
    }
  }
  .ui-datepicker-next {
    .ui-icon {
      background-image: url('../images/png/arrow.png');
    }
  }
}

@include breakpoint(screen-xs-min) {
  #ui-datepicker-div {
    width: auto;
  }

  .ui-datepicker {
    table.ui-datepicker-calendar {
      width: auto !important;
    }
  }
}

@media (max-width: 479px) {
  #ui-datepicker-div {
    left: 0 !important;
  }
}

