// in order to enable spinning apend .is-spinning class next to the .icon class
.is-spinning:before {
  @include animation(icon-spin-right 2s infinite linear);
}

@-webkit-keyframes icon-spin-right {
  0% {
    @include rotate(0deg);
  }

  100% {
    @include rotate(359deg)
  }
}

@keyframes icon-spin-right {
  0% {
    @include rotate(0deg);
  }

  100% {
    @include rotate(359deg)
  }
}

.inpage-nav__wrapper {
  margin-bottom: 1.667em;
  &.affix,
  &.affix-top {
    position:relative;
  }
  @extend .hidden-print;
}

.inpage-nav {
  .inpage-nav__list {
    margin: 0 0 0 -11px !important;
    padding: 0;
    list-style: none;
    list-style-image: none;

    li {
      &.active {
        .inpage_nav__list-item {
          border-color: $brand-primary;
          background: $grey-lighter;
        }
      }
    }
  }

  .inpage_nav__list-item {
    padding-top: 7px;
    padding-bottom: 7px;
    padding-left: 8px;
    display: block;
    color: #000;
    font-size: $font-size-little;
    font-weight: bold;
    border-left: 3px solid transparent;

    &:hover {
      background: none;
      text-decoration: underline;
    }

    &:focus {
      background: $yellow-light !important;
    }
  }
}

.inpage-nav__navbar {
  background-color: #ffffff;
  color: #ffffff;

  .inpage-nav__current-wrapper {
    overflow: hidden;
  }

  .inpage-nav__current,
  .inpage-nav__help {
    padding-bottom: 0px;
  }

  .inpage-nav__current {
    color: #ffffff;
    text-overflow: ellipsis;
    white-space: nowrap;
    display: block;
    width: 100%;
    overflow: hidden;
    font-weight: bold;
    font-size: 1em;
    cursor: pointer;
    padding-top: 1em;

    &:hover {
      color: #fff;
    }
  }

  .inpage-nav__help {
    display: none;
    background-color: $grey-lighter;
    font-size: $font-size-xsmall;
    text-transform: uppercase;
    color: $grey-dark;
    padding-top: 1.35em;
  }

  .inpage-nav__title {
    display: block;
    font-size: 1em;
    padding: 1em;
    line-height: 1;
    font-weight: bold;
  }

  .inpage-nav__toggle {
    @extend .btn;
    margin: 0;
    background-color: $blue-darkest;
    border-color: $blue-darkest;
    padding: 1em;
    &:hover,
    &:active,
    &:focus {
      background-color: $blue-darkest;
      text-decoration: none;
      color: #ffffff;
    }
  }

  .inpage-nav__icon-arrow {
    display: block;
    transition: all 0.3s ease 0s;
    transform-origin: 50% 50%;
    color: #ffffff;
    margin: 5px 4px;
    font-size: 10px;
    &:before {
      margin-right: 0px;
    }
  }

  .inpage-nav__close-text {
    display: none;
    font-size: $font-size-base;
    line-height: $line-height-computed;
  }

  .inpage-nav__header {
    margin: 0;
  }

  .inpage-nav__container {
    padding: 0;
    width: auto;
    background-color: $blue-dark;

    .navbar-collapse {
      max-height: none;
    }
  }

  .inpage-nav__list--navbar {
    padding: 0 15px;
    li {
      &.active {
        .inpage_nav__list-item {
          color: inherit;
          background: none;
        }
      }
      .inpage_nav__list-item {
        color: #fff;
        border-top: 1px solid $blue-darkest;
        padding: 0.8em 0 0.8em 1em;
        font-size: $font-size-little;

        &:hover {
          background: none;
          text-decoration: underline;
        }
      }
    }
  }
}

.inpage-nav__navbar-wrapper {
  display: none;
  &.is-active {
    display: inherit;
  }
  &.is-collapsed,
  &.is-collapsing {
    .inpage-nav__navbar {
      height: 100%;
    }
    .inpage-nav__help,
    .inpage-nav__close-text {
       display: block;
    }
    .inpage-nav__current {
     display: none;
    }
    .inpage-nav__header{
     background-color: $grey-lighter;
     font-weight: normal;
    }
    .inpage-nav__toggle {
      background-color: $grey;
      border-color: $grey;

      .inpage-nav__icon-arrow {
        @include rotate(180deg);
        color: $grey-dark;
      }
    }
  }
}

.inpage-nav__block-title {
  display: inherit;
  font-size: $font-size-base;
  text-transform: uppercase;
  font-weight: normal;
  color: $grey-dark;
}

.is-inpage-nav-open {
  overflow: hidden;

  .inpage-nav__navbar {
    overflow: auto;
  }
}

@include breakpoint(screen-md-min) {
  .inpage-nav__wrapper {
    &.affix,
    &.affix-top {
      top: 0;
    }

    &.affix {
      position: fixed;
      top: 0;
      padding-top: 30px;
    }

    &.affix-bottom {
      position: absolute;
    }
  }

  .inpage-nav__navbar-wrapper.is-active {
    display: none;
  }
}

