  list-style: none;
  margin: 0;
}

.site-switcher__option {
  display: inline-block;
  text-align: center;
  vertical-align: middle;
  width: 50%;

  & > a {
    display: block;
    width: 100%;
    padding: 0.5em 1.667em;
    line-height: 1.3;

    &:after, &.is-external:after {
      content: none !important;
    }
  }

  &.is-selected {
    background: $blue-dark;
    border-left: none;

    & > a {
      color: #fff;
      font-weight: bold;
    }
  }
}

.site-switcher--header {
  .site-switcher__list {
    &:first-child {
      border-left: 1px solid $grey;
    }
  }

  .site-switcher__option {
    &.is-selected {
      & > a {
        position: relative;

        &:after {
          top: 100%;
          left: 50%;
          border: solid transparent;
          content: " " !important;
          height: 0;
          width: 0;
          position: absolute;
          pointer-events: none;
          border-color: rgba(0, 68, 148, 0);
          border-top-color: $blue-dark;
          border-width: 10px;
          margin-left: -10px;
        }
      }
    }
  }
}

.site-switcher--footer {
  border-bottom: 1px solid $grey-lightest;
  overflow: visible;

  .site-switcher__list {
    @media (min-width: 510px) {
      & {
        float: right;
      }
    }


    &:first-child {
      border-left: none;
    }
  }

  .site-switcher__option {
    &.is-selected {
      & > a {
        position: relative;

        &:after {
          bottom: 100%;
          left: 50%;
          border: solid transparent;
          content: " " !important;
          height: 0;
          width: 0;
          position: absolute;
          pointer-events: none;
          border-color: rgba(0, 68, 148, 0);
          border-bottom-color: $blue-dark;
          border-width: 10px;
          margin-left: -10px;
        }
      }
    }
  }
}

@media (min-width: 510px) {
  .site-switcher__option {
    width: auto;
  }
}

.social-media-links {
  background-color: $grey-lighter;
  padding: 30px;
  .social-media-links__list {
    list-style-type: none;
    padding: 0px !important;
    margin: 0px !important;
  }
  @extend .hidden-print;
}

.social-media-links__label {
  margin-bottom: 0.8em;
  display: block;
  width: 100%;
}

.social-media-links__item {
  margin-bottom: 0.7em;
  & > a {
    width: 100%;
    display: block;

    &:before {
      margin-bottom: 0px;
    }
    &:after, &.is-external:after {
      display: none !important;
    }
  }
}

.social-media-links__item--noicon {
  font-size: 0.8em;
  margin-top: 1.2em;
}

// Local variables
$table-border: 1px solid #245898;
$padding-splash-xl: 2em; // 52px

.splash-page {

  &__site-header {
    background: #ffffff;

    .logo {
      margin: 16px auto 40px auto;
      float: none;
      background: url("../images/svg/logo.svg") center center no-repeat;
      background-origin: content-box;

      .no-svg & {
        background: url("../images/png/logo--big.png") center center no-repeat;
      }
    }

    @include breakpoint(screen-xs-min) {
      .logo {
        width: 290px;
        height: 153px;
      }
    }

    @include breakpoint(screen-sm-min) {
      .logo {
        width: 378px;
        height: 193px;
      }
    }

  }

  &__container {
    position: relative;
  }

  &__content {

    h2 {
      display: none;
      color: #ffffff;
      font-size: $font-size-mid;
      padding-top: $padding-splash-xl;
      padding-bottom: $padding-splash-xl;
      clear:both;
    }
  }

  &__helper-text {
    color: #ffffff;
    font-size: $font-size-little;
    margin-top: $padding-splash-xl;
    clear: both;

    p {
      max-width: 100%;
    }

  }

  &__btn-language {
    background: #ffffff;
    margin-bottom: 4px;
    width: 100%;
    text-align: left !important; // overriding bootstrap btn class

    &:hover {
      color: #000000;
      background: $yellow-light;
    }

    &.icon--check:before {
      display: none;
    }
  }

  &__btn-close {
    position: absolute;
    top: -15px;
    right: 0;
    padding: 0.5em 0 0.5em 1em;
    font-size: $font-size-little;
    font-weight: normal;
    color: #ffffff;
    display: none;

    &:hover {
      color: #ffffff;
    }

    &:after {
      content: "X";
      float: right;
      display: inline-block;
      border: 1px solid #ffffff;
      margin-left: 0.5em;
      width: 1.6em;
      height: 1.6em;
      border-radius: 1.1em;
      text-align: center;
    }
  }

  &--overlay {
    display: none;
    position: fixed;
    height: 100%;
    width: 100%;
    background: rgba(0, 68, 148, 0.95);
    overflow-x: auto;
    top: 0;
    left: 0;
    z-index: 10;
    text-align: left;

    h2 {
      display: block;
    }

    .splash-page__btn-language {
      &.is-active {
        color: #000000;
        background: $yellow-light;
        box-shadow: none;
      }
      &.icon--check:before {
        float: right;
        display: block;
      }
    }

    .splash-page__btn-close {
      display: block;
    }

  }
}

// Drupal-specific hacks
// specific to a case of having a page
body {
  &.disablescroll {
    overflow: hidden;
  }
}
.page-splash {
  background: rgba(0, 68, 148, 1);
}

.splash-page {
  &__helper-text {
    table {
      width: 100%;
    }
    tbody {
      border-bottom: $table-border;

      tr {
        border-top: $table-border;
        clear: both;

        th {
          vertical-align: top;
          min-width: 6em;
          font-weight: bold;
          padding-top: 0.6em;
        }

        td, th {
          padding: 0.6em 0 0.6em 0;
        }
      }
    }
  }
}

.tags {
  padding-bottom: 0.8em;
  width: 100%;
  overflow: hidden;
}

.tags__label {
  float: left;
  text-transform: uppercase;
  margin-right: 1em;
  margin-top: 0.1em;
}

.tags__items {
  float: left;
  overflow: hidden;
}

.tags__item {
  display: inline-block;
  margin-right: 0.4em;
  margin-bottom: 0.4em;
  a {
    background: $grey-light;
    border-radius: 5px;
    color: $text-color;
    display: inline-block;
    font-size: $font-size-little;
    line-height: 1;
    padding: 0.5em;
    text-transform: lowercase;

    &:hover,
    &:active,
    &:focus {
      text-decoration: underline;
    }
  }
}

.timeline {
  border-left: 4px solid $yellow-light;
  padding-left: 1.667em;
  padding-bottom: 0;
  padding-top: 2em;
  position: relative;

  .timeline__item-title {
    font-weight: bold;
    line-height: 1em;
    &:before {
      content: '';
      position: absolute;
      background: #fff;
      border: 3px solid $grey-dark;
      height: 14px;
      width: 14px;
      border-radius: 50%;
      left: -0.6em;
      margin-top: 0.2em;
    }
  }
  .timeline__text {
    margin-top: 0.2em;
    margin-bottom: 1em;
    ul,
    ol {
      padding-left: 0;
      list-style: none !important;
    }
    ul > li {
      color: $grey-dark !important;
    }
    & > * {
      margin-bottom: 0 !important;
    }
  }
}

.btn {
  &.btn-time-line {
    @extend .btn-ctn;
    margin-bottom: 0;

    &:after {
      content: '\e616';
    }
  }
}

.timeline--nopadding {
  padding-top: 0.5em;
}

.view-biography-timeline .view-footer {
  margin-top: 1em !important;
}

@include breakpoint(screen-xs-min) {
  .timeline {
    .timeline__item-title {
      &:before {
        margin-left: 2px;
      }
    }
  }
}

.top-image {
  overflow: hidden;

  picture {
    display: block;
    text-align: center;
  }
}
